create table public.app_settings (
  id uuid not null default gen_random_uuid (),
  setting_key character varying(50) not null,
  setting_value jsonb not null,
  description text null,
  category character varying(30) null,
  data_type character varying(20) not null,
  is_active boolean null default true,
  is_public boolean null default false,
  created_at timestamp with time zone null default now(),
  updated_at timestamp with time zone null default now(),
  constraint app_settings_pkey primary key (id),
  constraint app_settings_setting_key_key unique (setting_key),
  constraint app_settings_data_type_check check (
    (
      (data_type)::text = any (
        (
          array[
            'string'::character varying,
            'number'::character varying,
            'boolean'::character varying,
            'array'::character varying,
            'object'::character varying
          ]
        )::text[]
      )
    )
  ),
  constraint check_setting_value_type check (
    validate_setting_value ((data_type)::text, setting_value)
  )
) TABLESPACE pg_default;

create index IF not exists idx_app_settings_key on public.app_settings using btree (setting_key) TABLESPACE pg_default;

create index IF not exists idx_app_settings_category on public.app_settings using btree (category) TABLESPACE pg_default;

create index IF not exists idx_app_settings_active on public.app_settings using btree (is_active) TABLESPACE pg_default;

create trigger trigger_app_settings_updated_at BEFORE
update on app_settings for EACH row
execute FUNCTION update_app_settings_updated_at ();

